mapper.select:
 		SELECT * FROM
 			(SELECT ROWNUM R, B.* FROM
		  		(
			  		SELECT * FROM QNA 

			  		ORDER BY REF DESC, STEP ASC
		  		) B
	  		)
	  	WHERE R BETWEEN #{pager.startRow} AND #{pager.lastRow}

mapper.update:
UPDATE QNA
SET SUBJECT = #{subject}, CONTENTS = #{contents}
WHERE QNANUM = #{num} AND NAME = #{name}

UPDATE QNA
SET STEP = STEP+1
WHERE REF = #{ref}
AND STEP > #{step}
